import React from 'react'
import * as R from 'ramda'
import {connect} from 'react-redux'

import {getBasketPhonesWithCount, getTotalBasketPrice} from "selectors"
import {removePhoneFromBasket, basketCheckout, cleanBasket} from 'actions'
import {Link} from "react-router-dom";


const Basket = ({
                    phones,
                    totalPrice,
                    removePhoneFromBasket,
                    basketCheckout,
                    cleanBasket
                }) => {
    const isBasketEmpty = R.isEmpty(phones)
    const renderContent = () => (
        <div>
            {isBasketEmpty && <div>Your shoping card is empty</div>}
            <div className="table-responsive">
                <table className="table-bordered table-striped table-concedered cf">
                    <tbody>
                    {phones.map((phone, index) => (
                        <tr
                            key={index}
                            className="item-checout">
                            <td className="first-column-checkout">
                                <img
                                    className="img-thumbnail"
                                    src={phone.image}
                                    alt={phone.name}
                                />
                            </td>
                            <td>{phone.name}</td>
                            <td>${phone.price}</td>
                            <td>{phone.count}</td>
                            <td>
                                <span
                                    className="delete-cart"
                                    onClick={() => removePhoneFromBasket(phone.id)}
                                ></span>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
            {R.not(isBasketEmpty) &&
            <div className="row">
                <div className="pull-right total-user-checkout">
                    <b>Total:</b>
                    ${totalPrice}
                </div>
            </div>
            }
        </div>

    )

    const renderSidebar = () => (
        <div>
            <Link to="/" className="btn btn-info">
                <span className="glyphicon glyphicon-info-sign"></span>
                <span> Continue shoping!</span>
            </Link>
            {R.not(isBasketEmpty) &&
            <div>
                <button
                    onClick={cleanBasket}
                    className="btn btn-danger">
                    <span className="glyphicon glyphicon-trash"></span>
                    Clean Cart
                </button>
                <button
                    className="btn btn-success"
                    onClick={() => basketCheckout(phones)}
                >
                    <span className="glyphicon glyphicon-envelope"></span>
                    Checkout
                </button>
            </div>
            }
        </div>
    )


    //console.log(phones, totalPrice)
    return (
        <div className="view-container">
            <div className="container">
                <div className="row">
                    <div className="col-md-9">
                        {renderContent()}
                    </div>
                    <div className="col-md-3 btn-user-checkout">
                        {renderSidebar()}
                    </div>

                </div>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        phones: getBasketPhonesWithCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
}

const mapDispatchToProps = {
    removePhoneFromBasket,
    basketCheckout,
    cleanBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Basket)